import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      colors: Array(9).fill("white"),
      clickedOrder: [],
    };
  }

  handleClick = (index) => {
    const { colors, clickedOrder } = this.state;

    if (colors[index] === "white") {
      const newColors = [...colors];
      newColors[index] = "green";
      const newClickedOrder = [...clickedOrder, index];

      this.setState(
        { colors: newColors, clickedOrder: newClickedOrder },
        () => {
          if (newClickedOrder.length === 9) {
            this.changeToOrangeInOrder(newClickedOrder);
          }
        }
      );
    }
  };

  changeToOrangeInOrder = (order) => {
    order.forEach((idx, i) => {
      setTimeout(() => {
        const newColors = [...this.state.colors];
        newColors[idx] = "orange";
        this.setState({ colors: newColors });
      }, i * 500);
    });
  };

  render() {
    return (
      <div className="matrix">
        {this.state.colors.map((color, index) => (
          <div
            key={index}
            className="box"
            style={{ backgroundColor: color }}
            onClick={() => this.handleClick(index)}
          ></div>
        ))}
      </div>
    );
  }
}

export default App;
